import { Toaster } from "@/components/ui/toaster";
import { Toaster as Sonner } from "@/components/ui/sonner";
import { TooltipProvider } from "@/components/ui/tooltip";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";

// AJOUT ICI (NOUVEAU)
import { SettingsProvider } from "./context/SettingsContext"; // AJOUTÉ

import ForgotPassword from "./pages/ForgotPassword";
import ResetPassword from "./pages/ResetPassword";
import Login from "./pages/Login";
import NotFound from "./pages/NotFound";
import Register from "./pages/Register";
import EmailVerified from "./pages/EmailVerified";
import ConditionsUtilisation from "./pages/ConditionsUtilisation";
import PolitiqueConfidentialite from "./pages/PolitiqueConfidentialite";

import ProtectedRoute from "./components/ProtectedRoute";

import AgencyDashboard from "./pages/agency/AgencyDashboard";
import ClientsList from "./pages/agency/ClientsList";
import ClientProfile from "./pages/agency/ClientProfile";
import AgencyOffers from "./pages/agency/AgencyOffers";
import AgencySettings from "./pages/agency/AgencySettings";
import OfferProfil from "@/pages/agency/OfferProfil";
import PageProfil from "./pages/agency/PageProfil";
import Statistic from "./pages/agency/Statistic";
import NotificationsPage from "./pages/NotificationsPage";
import { AgencyLayout } from "./components/agency/AgencyLayout";


const queryClient = new QueryClient();

const App = () => (
  <QueryClientProvider client={queryClient}>
    <TooltipProvider>
      <AuthProvider>
        <SettingsProvider>
        <Toaster />
        <Sonner />
        <BrowserRouter>
          <Routes>
            {/* ✅ Routes publiques */}
            <Route path="/" element={<Login />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/email-verified" element={<EmailVerified />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/password-reset/:token" element={<ResetPassword />} />
            <Route path="/conditions-utilisation" element={<ConditionsUtilisation />} />
            <Route path="/politique-confidentialite" element={<PolitiqueConfidentialite />} />

            {/* ✅ Routes protégées AVEC permissions */}
            <Route
              path="/dashboard"
              element={
                <ProtectedRoute>
                  <AgencyDashboard />
                </ProtectedRoute>
              }
            />
            <Route
              path="/agency"
              element={
                <ProtectedRoute>
                  <AgencyDashboard />
                </ProtectedRoute>
              }
            />

            {/* ✅ Clients - besoin de la permission 'view_clients' */}
            <Route
              path="/agency/clients"
              element={
                <ProtectedRoute requiredPermission="view_clients">
                  <ClientsList />
                </ProtectedRoute>
              }
            />
            <Route
              path="/agency/clients/:id"
              element={
                <ProtectedRoute requiredPermission="view_clients">
                  <ClientProfile />
                </ProtectedRoute>
              }
            />

            {/* ✅ Offres - besoin de la permission 'view_offers' */}
            <Route
              path="/agency/offers"
              element={
                <ProtectedRoute requiredPermission="view_offers">
                  <AgencyOffers />
                </ProtectedRoute>
              }
            />
            <Route
              path="/agency/offres/:id"
              element={
                <ProtectedRoute requiredPermission="view_offers">
                  <OfferProfil />
                </ProtectedRoute>
              }
            />

            {/* ✅ Paramètres - besoin de la permission 'view_settings' */}
            <Route
              path="/agency/settings"
              element={
                <ProtectedRoute requiredPermission="view_settings">
                  <AgencySettings />
                </ProtectedRoute>
              }
            />

            {/* ✅ Statistiques - besoin de la permission 'view_statistics' */}
            <Route
              path="/agency/Statistic"
              element={
                <ProtectedRoute requiredPermission="view_statistics">
                  <AgencyLayout>
                    <Statistic />
                  </AgencyLayout>
                </ProtectedRoute>
              }
            />

            {/* ✅ Notifications - accessible à tous les utilisateurs connectés */}
            <Route
              path="/agency/notifications"
              element={
                <ProtectedRoute>
                  <AgencyLayout>
                    <NotificationsPage />
                  </AgencyLayout>
                </ProtectedRoute>
              }
            />

            {/* ✅ Profil - accessible à tous les utilisateurs connectés */}
            <Route
              path="/profile"
              element={
                <ProtectedRoute>
                  <PageProfil />
                </ProtectedRoute>
              }
            />

            {/* ✅ 404 */}
            <Route path="*" element={<NotFound />} />
          </Routes>
        </BrowserRouter>
        </SettingsProvider> 
      </AuthProvider>
    </TooltipProvider>
  </QueryClientProvider>
);

export default App;