import { 
  LayoutDashboard, 
  Users, 
  ShoppingCart, 
  Settings, 
  Building2
} from "lucide-react";
import { NavLink, useLocation, useNavigate } from "react-router-dom";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarTrigger,
  useSidebar,
} from "@/components/ui/sidebar";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { BarChart3 } from "lucide-react";

const navigationItems = [
  { 
    title: "Dashboard", 
    url: "/agency", 
    icon: LayoutDashboard,
    badge: null
  },
  { 
    title: "Clients", 
    url: "/agency/clients", 
    icon: Users,
    badge: ""
  },
  { 
    title: "Offres", 
    url: "/agency/offers", 
    icon: ShoppingCart,
    badge: ""
  },
  { 
    title: "Statistique", 
    url: "/agency/statistic", 
    icon: BarChart3,
    badge: null
  },
  { 
    title: "Param√®tres", 
    url: "/agency/settings", 
    icon: Settings,
    badge: null
  },
];

// üî• CORRECTION : Export par d√©faut au lieu d'export nomm√©
export default function AgencySidebar() {
  const { state } = useSidebar();
  const location = useLocation();
  const navigate = useNavigate();
  const currentPath = location.pathname;
  const collapsed = state === "collapsed";

  // Fonction corrig√©e pour d√©tecter l'√©l√©ment actif
  const isActive = (path: string) => {
    if (path === "/agency") {
      // Pour le dashboard, on veut une correspondance exacte
      return currentPath === path;
    }
    // Pour les autres pages, on v√©rifie si le chemin commence par l'URL
    return currentPath.startsWith(path);
  };

  const getNavClasses = (path: string) => {
    const baseClasses = "w-full justify-start transition-smooth hover:bg-primary/5";
    return isActive(path) 
      ? `${baseClasses} bg-primary/10 text-primary border-r-2 border-primary shadow-sm` 
      : `${baseClasses} text-muted-foreground hover:text-foreground`;
  };

  // Fonction pour g√©rer le retour en arri√®re
  const handleLogoClick = () => {
    navigate(-1); // Retour √† la page pr√©c√©dente
  };

  return (
    <Sidebar className="border-r border-border bg-card shadow-soft" collapsible="icon">
      {/* Agency Header */}
      <div className="p-4 border-b border-border">
        <div className="flex items-center gap-3">
          {/* Logo cliquable pour retour en arri√®re */}
          <button 
            onClick={handleLogoClick}
            className="p-2 bg-gradient-primary rounded-lg hover:bg-gradient-primary/80 transition-all duration-200 cursor-pointer focus:outline-none focus:ring-2 focus:ring-primary/20"
            title="Retour en arri√®re"
          >
            <Building2 className="h-5 w-5 text-primary-foreground" />
          </button>
          {!collapsed && (
            <div>
              <h2 className="font-semibold text-foreground">Mon Agence</h2>
              <p className="text-xs text-muted-foreground">Espace Pro</p>
            </div>
          )}
        </div>
      </div>

      <SidebarContent className="px-2 py-4">
        <SidebarGroup>
          <SidebarGroupLabel className="px-3 py-2 text-xs font-medium text-muted-foreground uppercase tracking-wider">
            Navigation
          </SidebarGroupLabel>

          <SidebarGroupContent>
            <SidebarMenu className="space-y-1">
              {navigationItems.map((item) => (
                <SidebarMenuItem key={item.title}>
                  <SidebarMenuButton asChild>
                    <NavLink 
                      to={item.url} 
                      className={getNavClasses(item.url)}
                    >
                      <item.icon className="h-4 w-4 shrink-0" />
                      <span className="flex-1">{item.title}</span>
                      {item.badge && (
                        <Badge 
                          variant="secondary" 
                          className="ml-auto bg-primary/10 text-primary text-xs"
                        >
                          {item.badge}
                        </Badge>
                      )}
                    </NavLink>
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>

      {/* Collapse Button */}
      <div className="p-4 border-t border-border">
        <SidebarTrigger className="w-full" />
      </div>
    </Sidebar>
  );
}