// hooks/useUserPermissions.ts
import { useState, useEffect } from 'react';

export function useUserPermissions() {
  const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('user') || '{}');
    } catch {
      return {};
    }
  });

  useEffect(() => {
    const handleStorageChange = () => {
      try {
        const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
        setUser(currentUser);
      } catch (error) {
        console.error('Error parsing user from localStorage:', error);
      }
    };

    // Écouter les changements de localStorage
    window.addEventListener('storage', handleStorageChange);
    // Écouter nos événements personnalisés
    window.addEventListener('userPermissionsUpdated', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('userPermissionsUpdated', handleStorageChange);
    };
  }, []);

  const hasPermission = (permission: string) => {
    return user.role === 'admin' || (user.permissions && user.permissions.includes(permission));
  };

  const isAdmin = () => {
    return user.role === 'admin';
  };

  const refreshPermissions = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await fetch('http://127.0.0.1:8000/api/user', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const userData = await response.json();
        localStorage.setItem('user', JSON.stringify(userData));
        window.dispatchEvent(new Event('userPermissionsUpdated'));
      }
    } catch (error) {
      console.error('Error refreshing permissions:', error);
    }
  };

  return {
    user,
    hasPermission,
    isAdmin,
    refreshPermissions
  };
}