// Helper central pour construire les URLs d'API Ã  partir des variables Vite
const VITE_BACKEND_URL = (import.meta.env.VITE_BACKEND_URL as string) || "http://127.0.0.1:8000";
const VITE_API_PREFIX = (import.meta.env.VITE_API_PREFIX as string) || "/api";

export const API_BASE = `${VITE_BACKEND_URL.replace(/\/$/, "")}${VITE_API_PREFIX}`;

export function apiUrl(path: string) {
  const cleaned = path.startsWith("/") ? path : `/${path}`;
  return `${API_BASE}${cleaned}`;
}

export default { API_BASE, apiUrl };
