import { useState, useEffect } from "react";
import axios from "axios";
import { apiUrl } from "@/lib/config";
import { Mail, Loader2, RefreshCw } from "lucide-react";

export default function ForgotPassword() {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Ajouts pour countdown + resend
  const [countdown, setCountdown] = useState(60);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [canResend, setCanResend] = useState(false);

  useEffect(() => {
    let timer: NodeJS.Timeout;

    if (isSubmitted && countdown > 0) {
      timer = setInterval(() => {
        setCountdown((prev) => prev - 1);
      }, 1000);
    }

    return () => clearInterval(timer);
  }, [isSubmitted, countdown]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setMessage("");
    setError("");
    setLoading(true);

    try {
      const res = await axios.post(apiUrl("/forgot-password"), { email });

      if (res.status === 200) {
        setMessage("Un email de réinitialisation a été envoyé si l'adresse existe.");
        setIsSubmitted(true);
        setCountdown(60);
        setCanResend(false);

        // Activer renvoi après 3s
        setTimeout(() => setCanResend(true), 3000);
      }
    } catch (err: unknown) {
      if (err instanceof Error) setError(err.message);
      else setError("Une erreur est survenue. Veuillez réessayer.");
    } finally {
      setLoading(false);
    }
  };

  const handleResend = async () => {
    setLoading(true);
    setError("");
    setMessage("");

    try {
      const res = await axios.post(apiUrl("/forgot-password"), { email });

      if (res.status === 200) {
        setMessage(`Nouvel email envoyé à ${email}`);
        setCountdown(60);
        setCanResend(false);
        setTimeout(() => setCanResend(true), 3000);
      }
    } catch (err: unknown) {
      setError("Impossible de renvoyer l'email. Veuillez patienter!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 flex items-center justify-center p-4">
      <div className="relative w-full max-w-md">
        <form
          onSubmit={handleSubmit}
          className="bg-white rounded-2xl shadow-lg p-8 border border-gray-200"
        >
          {/* En-tête */}
          <div className="text-center mb-8">
            <h1 className="text-2xl font-bold text-gray-900 mb-2">
              Réinitialiser le mot de passe
            </h1>
            <p className="text-gray-600 text-sm">Recevez un lien par email</p>
          </div>

          {/* Messages */}
          {message && (
            <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg text-green-700 text-center">
              {message}
            </div>
          )}
          {error && (
            <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg text-red-700 text-center">
              {error}
            </div>
          )}

          {!isSubmitted ? (
            <>
              {/* Champ email */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-3">
                  Email
                </label>
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Mail className="h-5 w-5 text-gray-400" />
                  </div>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="block w-full pl-11 pr-4 py-3 bg-white border border-gray-300 rounded-lg focus:ring-2 focus:ring-gray-400 focus:border-gray-400 transition-all duration-200 outline-none placeholder-gray-400"
                    placeholder="votre@email.com"
                    required
                  />
                </div>
              </div>

              {/* Bouton d'envoi */}
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-gray-900 hover:bg-gray-800 text-white py-3.5 px-4 rounded-lg font-medium shadow-md hover:shadow-lg transition-all duration-200 disabled:opacity-50 flex items-center justify-center"
              >
                {loading ? (
                  <>
                    <Loader2 className="animate-spin h-5 w-5 mr-2" />
                    <span>Envoi en cours...</span>
                  </>
                ) : (
                  "Envoyer le lien"
                )}
              </button>
            </>
          ) : (
            <>
              {/* Compte à rebours */}
              {countdown > 0 && (
                <div className="mb-6 p-4 bg-gray-50 border border-gray-200 rounded-lg">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center">
                      <RefreshCw className="h-5 w-5 mr-2 text-gray-600" />
                      <span className="text-sm font-medium text-gray-800">Temps restant :</span>
                    </div>
                    <span className="text-2xl font-bold text-gray-900">{countdown}s</span>
                  </div>

                  {/* Barre de progression */}
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div
                      className="bg-gray-700 h-2 rounded-full transition-all duration-1000 ease-linear"
                      style={{ width: `${(countdown / 60) * 100}%` }}
                    ></div>
                  </div>

                  <p className="text-xs text-gray-600 mt-2 text-center">
                    {countdown <= 10
                      ? `${countdown}s restants`
                      : "Vérifiez votre email rapidement"}
                  </p>
                </div>
              )}

              {/* Bouton renvoi */}
              {canResend ? (
                <button
                  type="button"
                  onClick={handleResend}
                  disabled={loading}
                  className="w-full bg-gray-700 hover:bg-gray-600 text-white py-3.5 px-4 rounded-lg font-medium shadow-md hover:shadow-lg transition-all duration-200 disabled:opacity-50 flex items-center justify-center"
                >
                  {loading ? (
                    <>
                      <Loader2 className="animate-spin h-5 w-5 mr-2" />
                      <span>Envoi en cours...</span>
                    </>
                  ) : (
                    <>
                      <Mail className="h-5 w-5 mr-2" />
                      <span>Renvoyer l'email</span>
                    </>
                  )}
                </button>
              ) : (
                <div className="mb-6 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                  <div className="flex items-center justify-center text-sm">
                    <RefreshCw className="animate-spin h-4 w-4 mr-2 text-yellow-600" />
                    <span className="text-yellow-800 font-medium">Activation du renvoi...</span>
                  </div>
                </div>
              )}
            </>
          )}
        </form>
      </div>
    </div>
  );
}