import React, { useState } from "react";
import { useParams, useSearchParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { apiUrl } from "@/lib/config";
import { Lock, Eye, EyeOff, Loader2 } from "lucide-react";

interface ResetResponse {
  status: string;
}
interface ErrorResponse {
  message: string;
}

const ResetPassword: React.FC = () => {
  const { token } = useParams<{ token: string }>();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const email = searchParams.get("email") || "";

  const [password, setPassword] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState("");
  const [message, setMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);
  const [error, setError] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);

  // ✅ Vérification des critères
  const criteria = [
    { test: password.length >= 8, label: "Au moins 8 caractères" },
    { test: /[A-Z]/.test(password), label: "Une majuscule" },
    { test: /[a-z]/.test(password), label: "Une minuscule" },
    { test: /[0-9]/.test(password), label: "Un chiffre" },
  ];

  const unmetCriteria = criteria.filter((c) => !c.test);
  const allCriteriaMet = unmetCriteria.length === 0;
  const passwordsMatch = password === passwordConfirmation && passwordConfirmation !== "";

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
  await axios.post<ResetResponse>(apiUrl("/reset-password"), {
        token,
        email,
        password,
        password_confirmation: passwordConfirmation,
      });

      setMessage("Mot de passe réinitialisé avec succès !");
      setIsSuccess(true);
      setTimeout(() => navigate("/login"), 2000);
    } catch (error: unknown) {
      if (typeof error === "object" && error !== null && "response" in error) {
        const serverError = error as { response?: { data?: ErrorResponse } };
        setMessage(serverError.response?.data?.message || "Erreur lors de la réinitialisation.");
      } else {
        setMessage("Erreur inconnue.");
      }
      setIsSuccess(false);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-indigo-100 via-purple-100 to-pink-100">
      <div className="w-full max-w-md bg-white p-8 rounded-2xl shadow-xl">
        <div className="text-center mb-6">
          <Lock className="mx-auto h-12 w-12 text-indigo-600" />
          <h2 className="text-2xl font-bold mt-2 text-gray-800">Réinitialiser le mot de passe</h2>
          <p className="text-gray-500 text-sm">Choisissez un nouveau mot de passe sécurisé</p>
        </div>

        {message && (
          <p className={`mb-4 text-center font-semibold ${isSuccess ? "text-green-600" : "text-red-500"}`}>
            {message}
          </p>
        )}

        <form onSubmit={handleSubmit}>
          {/* Nouveau mot de passe */}
          <div className="mb-4">
            <label className="block text-gray-700 font-medium">Nouveau mot de passe</label>
            <div className="relative flex items-center">
              <Lock className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
              <input
                type={showPassword ? "text" : "password"}
                className="w-full pl-10 pr-10 p-2 border rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute right-3 top-2.5 text-gray-500 hover:text-gray-700"
              >
                {showPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
              </button>
            </div>

            {/* ✅ Affiche seulement les critères NON remplis */}
            {password && unmetCriteria.length > 0 && (
              <ul className="mt-2 text-sm text-red-500 space-y-1">
                {unmetCriteria.map((c, index) => (
                  <li key={index}>• {c.label}</li>
                ))}
              </ul>
            )}
          </div>

          {/* Confirmation */}
          <div className="mb-6">
            <label className="block text-gray-700 font-medium">Confirmer le mot de passe</label>
            <div className="relative flex items-center">
              <Lock className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
              <input
                type={showConfirmPassword ? "text" : "password"}
                className="w-full pl-10 pr-10 p-2 border rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none"
                value={passwordConfirmation}
                onChange={(e) => setPasswordConfirmation(e.target.value)}
                required
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-500 hover:text-gray-700 transition-colors"
              >
                {showPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
              </button>
            </div>

            {/* ✅ Affiche seulement les critères NON remplis */}
            {password && unmetCriteria.length > 0 && (
              <div className="mt-3 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <h4 className="text-sm font-medium text-yellow-800 mb-2">Critères manquants :</h4>
                <ul className="text-sm text-yellow-700 space-y-1">
                  {unmetCriteria.map((c, index) => (
                    <li key={index}>• {c.label}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* Champ confirmation */}
          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Confirmer le mot de passe
            </label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Lock className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type={showConfirmPassword ? "text" : "password"}
                value={passwordConfirmation}
                onChange={(e) => setPasswordConfirmation(e.target.value)}
                className="block w-full pl-11 pr-11 py-3 bg-white border border-gray-300 rounded-lg focus:ring-2 focus:ring-gray-400 focus:border-gray-400 transition-all duration-200 outline-none placeholder-gray-400"
                placeholder="Confirmez votre mot de passe"
                required
              />
              <button
                type="button"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-500 hover:text-gray-700 transition-colors"
              >
                {showConfirmPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
              </button>
            </div>

            {/* Message de correspondance */}
            {passwordConfirmation && !passwordsMatch && (
              <div className="mt-2 p-2 bg-red-50 border border-red-200 rounded-lg">
                <p className="text-sm text-red-700 text-center">
                  Les mots de passe ne correspondent pas
                </p>
              </div>
            )}
          </div>

          {/* Bouton de soumission */}
          <button
            type="submit"
            disabled={loading || !allCriteriaMet || !passwordsMatch}
            className="w-full bg-gray-900 hover:bg-gray-800 text-white py-3.5 px-4 rounded-lg font-medium shadow-md hover:shadow-lg transition-all duration-200 disabled:opacity-50 flex items-center justify-center"
          >
            {loading ? (
              <>
                <Loader2 className="animate-spin h-5 w-5 mr-2" />
                <span>Réinitialisation...</span>
              </>
            ) : (
              "Réinitialiser le mot de passe"
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default ResetPassword;
