import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Users, DollarSign, Clock, Star, Calendar } from "lucide-react";
import { AgencyLayout } from "@/components/agency/AgencyLayout";
import axios from "axios";
import { useTranslation } from "react-i18next";

type Client = { id: number; name: string; company?: string; email: string; phone?: string; status: "active" | "inactive" | "pending"; projects: number; lastContact?: string | null; avatar?: string; created_at?: string; updated_at?: string; };
type Offer = { id: number; title: string; description: string; price: string; category: string; validUntil?: string; status: "active" | "pending" | "unknown"; clientsCount: number; };

export default function AgencyDashboard() {
  const { t } = useTranslation(["dashboard","common"]);
  const [stats, setStats] = useState({ activeClients: 0, monthlyRevenue: 0, revenueChange: 0, ongoingProjects: 0 });
  const [loading, setLoading] = useState(true);
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const headers = { Authorization: `Bearer ${token}` };
        const [clientsRes, offersRes] = await Promise.all([
          axios.get<Client[]>("http://127.0.0.1:8000/api/clients", { headers }),
          axios.get<Offer[]>("http://127.0.0.1:8000/api/offers", { headers }),
        ]);

        const activeClients = clientsRes.data.filter(c => c.status === "active").length;
        const ongoingProjects = clientsRes.data.reduce((sum, c) => sum + c.projects, 0);
        const totalRevenue = offersRes.data.reduce((sum, offer) => {
          const price = parseFloat(offer.price.replace(/[^0-9.-]+/g, "")) || 0;
          return sum + price * offer.clientsCount;
        }, 0);

        setStats({ activeClients, monthlyRevenue: totalRevenue, revenueChange: 0, ongoingProjects });
      } catch (err) {
        console.error("Erreur chargement stats", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const statCards = [
    { title: t("activeClients"), value: loading ? "..." : stats.activeClients.toString(), change: "+2 ce mois", icon: Users, color: "primary" },
    { title: t("revenue"), value: loading ? "..." : `€${stats.monthlyRevenue.toLocaleString("fr-FR")}`, change: t("dashboard.totalRevenue"), icon: DollarSign, color: "success" },
    { title: t("ongoingProjects"), value: loading ? "..." : stats.ongoingProjects.toString(), change: "3 à livrer cette semaine", icon: Clock, color: "warning" },
    { title: t("satisfaction"), value: "4.8/5", change: "Basé sur 24 avis", icon: Star, color: "accent" },
  ];

  const recentClients = [
    { name: "TechCorp", project: "Site E-commerce", status: "En cours", progress: 75 },
    { name: "StartupXYZ", project: "Application Mobile", status: "Validation", progress: 90 },
    { name: "LocalBiz", project: "Site Vitrine", status: "Nouveau", progress: 25 },
  ];

  const upcomingTasks = [
    { task: "Livraison projet TechCorp", due: "Demain", priority: "high" },
    { task: "Réunion client StartupXYZ", due: "Vendredi", priority: "medium" },
    { task: "Révisions LocalBiz", due: "Lundi prochain", priority: "low" },
  ];

  return (
    <AgencyLayout>
      <div className="space-y-6">
        <div className="bg-gradient-subtle rounded-lg p-6 border border-border">
          <h1 className="text-2xl font-bold text-foreground">
            {t("welcome", { name: user?.name || "Utilisateur" })}
          </h1>
          <p className="text-muted-foreground">{t("subtitle")}</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {statCards.map((stat, i) => (
            <Card key={i} className="border-border shadow-soft hover:shadow-medium transition-smooth">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium text-muted-foreground">{stat.title}</CardTitle>
                <div className={`p-2 rounded-lg bg-${stat.color}/10`}><stat.icon className={`h-4 w-4 text-${stat.color}`} /></div>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-foreground">{stat.value}</div>
                <p className="text-xs text-muted-foreground">{stat.change}</p>
              </CardContent>
            </Card>
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card className="border-border shadow-soft">
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                {t("recentClients")}
                <Button variant="ghost" size="sm">{t("viewAll", { ns: "common" })}</Button>
              </CardTitle>
              <CardDescription>{t("recentClientsDesc")}</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {recentClients.map((c, i) => (
                <div key={i} className="flex items-center justify-between p-3 rounded-lg bg-card-subtle border border-border">
                  <div className="space-y-1">
                    <p className="font-medium text-foreground">{c.name}</p>
                    <p className="text-sm text-muted-foreground">{c.project}</p>
                    <div className="flex items-center gap-2">
                      <Progress value={c.progress} className="w-20" />
                      <span className="text-xs text-muted-foreground">{c.progress}%</span>
                    </div>
                  </div>
                  <Badge variant={c.status === "En cours" ? "default" : c.status === "Nouveau" ? "secondary" : "outline"}>{c.status}</Badge>
                </div>
              ))}
            </CardContent>
          </Card>

          <Card className="border-border shadow-soft">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calendar className="h-5 w-5" /> {t("upcomingTasks")}
              </CardTitle>
              <CardDescription>{t("upcomingTasksDesc")}</CardDescription>
            </CardHeader>
            <CardContent className="space-y-3">
              {upcomingTasks.map((task, i) => (
                <div key={i} className="flex items-center justify-between p-3 rounded-lg bg-card-subtle border border-border">
                  <div className="space-y-1">
                    <p className="font-medium text-foreground">{task.task}</p>
                    <p className="text-sm text-muted-foreground">{task.due}</p>
                  </div>
                  <Badge variant={task.priority === "high" ? "destructive" : task.priority === "medium" ? "default" : "secondary"}>
                    {task.priority === "high" ? t("urgent") : task.priority === "medium" ? t("medium") : t("low")}
                  </Badge>
                </div>
              ))}
            </CardContent>
          </Card>
        </div>
      </div>
    </AgencyLayout>
  );
}