import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { AgencyLayout } from "@/components/agency/AgencyLayout";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  ArrowLeft,
  Edit,
  Save,
  X,
  Building2,
  Mail,
  Phone,
  Calendar,
  MapPin,
  User,
  Briefcase,
  MessageSquare,
  FileText,
  CreditCard,
  Clock,
  Eye,
  Star,
  Target
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import axios from "axios";
import { Client } from "@/types/clients";
import { useTranslation } from "react-i18next";

const API_URL = "http://127.0.0.1:8000/api/clients";

// Définir le type pour le statut
type ClientStatus = "active" | "inactive" | "pending";

export default function ClientProfile() {
  const { t } = useTranslation("clients");
  const { id } = useParams<{ id: string }>();
  const { toast } = useToast();

  const [client, setClient] = useState<Client | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState<Partial<Client>>({});
  const [isLoading, setIsLoading] = useState(true);

  // Charger le client depuis l'API
  useEffect(() => {
    const fetchClient = async () => {
      try {
        setIsLoading(true);
        const res = await axios.get<Client>(`${API_URL}/${id}`);
        console.log('Données client chargées:', res.data); // DEBUG
        setClient(res.data);
        setFormData(res.data);
      } catch (error) {
        console.error("Erreur lors du chargement du client", error);
        toast({
          title: t("error"),
          description: t("profileLoadError"),
          variant: "destructive",
        });
      } finally {
        setIsLoading(false);
      }
    };

    if (id) fetchClient();
  }, [id, toast, t]);

  const handleSave = async () => {
    try {
      if (!id) return;
      console.log('Données envoyées:', formData); // DEBUG
      const res = await axios.put<Client>(`${API_URL}/${id}`, formData);
      console.log('Réponse API:', res.data); // DEBUG
      setClient(res.data);
      setIsEditing(false);
      toast({
        title: t("profileUpdated"),
        description: t("profileUpdatedDesc"),
      });
    } catch (error) {
      console.error("Erreur lors de la sauvegarde", error);
      toast({
        title: t("error"),
        description: t("profileSaveError"),
        variant: "destructive",
      });
    }
  };

  const handleCancel = () => {
    if (client) setFormData(client);
    setIsEditing(false);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "active":
        return <Badge className="bg-green-100 text-green-800 border-green-200">{t("active")}</Badge>;
      case "inactive":
        return <Badge className="bg-red-100 text-red-800 border-red-200">{t("inactive")}</Badge>;
      case "pending":
        return <Badge className="bg-yellow-100 text-yellow-800 border-yellow-200">{t("pending")}</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  // Fonction pour générer les initiales du nom
  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  if (isLoading) {
    return (
      <AgencyLayout>
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center space-y-4">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
            <p className="text-muted-foreground">{t("loading")}...</p>
          </div>
        </div>
      </AgencyLayout>
    );
  }

  if (!client) {
    return (
      <AgencyLayout>
        <div className="text-center space-y-4 py-12">
          <User className="h-16 w-16 text-muted-foreground mx-auto" />
          <h2 className="text-2xl font-bold text-foreground">{t("profileNotFound")}</h2>
          <p className="text-muted-foreground">{t("profileNotFoundDesc")}</p>
          <Button asChild>
            <Link to="/agency/clients">
              <ArrowLeft className="mr-2 h-4 w-4" />
              {t("profileBack")}
            </Link>
          </Button>
        </div>
      </AgencyLayout>
    );
  }

  return (
    <AgencyLayout>
      <div className="space-y-6">
        {/* Header amélioré */}
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-6">
          <div className="flex items-center gap-4">
            <Button variant="outline" size="sm" asChild className="shadow-sm">
              <Link to="/agency/clients">
                <ArrowLeft className="mr-2 h-4 w-4" />
                {t("profileBack")}
              </Link>
            </Button>
            <div className="flex items-center gap-4">
              <Avatar className="h-16 w-16 border-4 border-background shadow-lg">
                <AvatarImage 
                  src={client.avatar || ""} 
                  alt={`Avatar de ${client.name}`}
                />
                <AvatarFallback className="bg-gradient-to-br from-primary to-primary/80 text-primary-foreground text-lg font-bold">
                  {getInitials(client.name)}
                </AvatarFallback>
              </Avatar>
              <div className="space-y-2">
                <div className="flex items-center gap-3">
                  <h1 className="text-2xl font-bold text-foreground">{client.name}</h1>
                  {getStatusBadge(client.status)}
                </div>
                <div className="flex items-center gap-4 text-sm text-muted-foreground">
                  <div className="flex items-center gap-1">
                    <Building2 className="h-4 w-4" />
                    <span>{client.company || t("profileNoCompany")}</span>
                  </div>
                  <div className="flex items-center gap-1">
                    <Mail className="h-4 w-4" />
                    <span>{client.email}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="flex items-center gap-3">
            {isEditing ? (
              <div className="flex gap-2">
                <Button size="sm" onClick={handleSave} className="bg-green-600 hover:bg-green-700 shadow-sm">
                  <Save className="mr-2 h-4 w-4" />
                  {t("save")}
                </Button>
                <Button size="sm" variant="outline" onClick={handleCancel} className="shadow-sm">
                  <X className="mr-2 h-4 w-4" />
                  {t("cancel")}
                </Button>
              </div>
            ) : (
              <Button 
                size="sm" 
                onClick={() => setIsEditing(true)} 
                className="bg-primary hover:bg-primary/90 shadow-sm"
              >
                <Edit className="mr-2 h-4 w-4" />
                {t("profileEdit")}
              </Button>
            )}
          </div>
        </div>

        {/* Statistiques rapides */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Card className="bg-gradient-to-br from-blue-50 to-blue-100 border-blue-200">
            <CardContent className="p-4 flex items-center gap-4">
              <div className="p-3 bg-blue-100 rounded-lg">
                <Target className="h-6 w-6 text-blue-600" />
              </div>
              <div>
                <p className="text-sm text-blue-600 font-medium">{t("status")}</p>
                <p className="text-lg font-bold text-blue-900 capitalize">
                  {client.status || t("profileUndefined")}
                </p>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-gradient-to-br from-purple-50 to-purple-100 border-purple-200">
            <CardContent className="p-4 flex items-center gap-4">
              <div className="p-3 bg-purple-100 rounded-lg">
                <Building2 className="h-6 w-6 text-purple-600" />
              </div>
              <div>
                <p className="text-sm text-purple-600 font-medium">{t("company")}</p>
                <p className="text-lg font-bold text-purple-900">
                  {client.company || "—"}
                </p>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-gradient-to-br from-green-50 to-green-100 border-green-200">
            <CardContent className="p-4 flex items-center gap-4">
              <div className="p-3 bg-green-100 rounded-lg">
                <Phone className="h-6 w-6 text-green-600" />
              </div>
              <div>
                <p className="text-sm text-green-600 font-medium">{t("contact")}</p>
                <p className="text-lg font-bold text-green-900">
                  {client.phone || t("profileNoPhone")}
                </p>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Onglets améliorés */}
        <Tabs defaultValue="overview" className="space-y-6">
          <TabsList className="grid w-full grid-cols-2 lg:w-auto lg:inline-flex">
            <TabsTrigger value="overview" className="flex items-center gap-2">
              <Eye className="h-4 w-4" />
              {t("profileOverview")}
            </TabsTrigger>

            <TabsTrigger value="notes" className="flex items-center gap-2">
              <MessageSquare className="h-4 w-4" />
              {t("profileNotes")}
            </TabsTrigger>
          </TabsList>

          {/* Vue d'ensemble améliorée */}
          <TabsContent value="overview" className="space-y-6">
            <Card>
              <CardHeader className="bg-muted/50 border-b">
                <CardTitle className="flex items-center gap-2 text-lg">
                  <User className="h-5 w-5 text-primary" />
                  {t("profileInfo")}
                </CardTitle>
                <CardDescription>
                  {t("profileInfoDesc")}
                </CardDescription>
              </CardHeader>
              <CardContent className="p-6">
                {isEditing ? (
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    <div className="space-y-4">
                      <div>
                        <Label htmlFor="name" className="text-sm font-medium">{t("fullName")}</Label>
                        <Input
                          id="name"
                          value={formData.name || ""}
                          onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                          className="mt-1"
                        />
                      </div>
                      <div>
                        <Label htmlFor="company" className="text-sm font-medium">{t("company")}</Label>
                        <Input
                          id="company"
                          value={formData.company || ""}
                          onChange={(e) => setFormData(prev => ({ ...prev, company: e.target.value }))}
                          className="mt-1"
                        />
                      </div>
                      <div>
                        <Label htmlFor="avatar" className="text-sm font-medium">{t("profileAvatarUrl")}</Label>
                        <Input
                          id="avatar"
                          type="url"
                          value={formData.avatar || ""}
                          onChange={(e) => setFormData(prev => ({ ...prev, avatar: e.target.value }))}
                          className="mt-1"
                          placeholder="https://example.com/avatar.jpg"
                        />
                      </div>
                    </div>
                    <div className="space-y-4">
                      <div>
                        <Label htmlFor="email" className="text-sm font-medium">{t("email")}</Label>
                        <Input
                          id="email"
                          type="email"
                          value={formData.email || ""}
                          onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                          className="mt-1"
                        />
                      </div>
                      <div>
                        <Label htmlFor="phone" className="text-sm font-medium">{t("phone")}</Label>
                        <Input
                          id="phone"
                          value={formData.phone || ""}
                          onChange={(e) => setFormData(prev => ({ ...prev, phone: e.target.value }))}
                          className="mt-1"
                        />
                      </div>
                      <div>
                        <Label htmlFor="status" className="text-sm font-medium">{t("status")}</Label>
                        <select
                          id="status"
                          value={formData.status || ""}
                          onChange={(e) => setFormData(prev => ({ 
                            ...prev, 
                            status: e.target.value as ClientStatus 
                          }))}
                          className="mt-1 flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2"
                        >
                          <option value="active">{t("active")}</option>
                          <option value="inactive">{t("inactive")}</option>
                          <option value="pending">{t("pending")}</option>
                        </select>
                        <div>
                          <Label className="text-sm font-medium">{t("projectsCount")}</Label>
                          <div className="flex items-center gap-2 mt-1">
                            <Button
                              size="icon"
                              variant="outline"
                              onClick={() => setFormData({ ...formData, projects: Math.max(0, (formData.projects ?? 0) - 1) })}
                            >
                              -
                            </Button>
                            <Input
                              type="number"
                              min="0"
                              value={formData.projects ?? 0}
                              onChange={(e) => setFormData({ ...formData, projects: parseInt(e.target.value) || 0 })}
                              className="w-20 text-center"
                            />
                            <Button
                              size="icon"
                              variant="outline"
                              onClick={() => setFormData({ ...formData, projects: (formData.projects ?? 0) + 1 })}
                            >
                              +
                            </Button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                    <div className="space-y-6">
                      <div className="flex items-center gap-3 p-4 bg-muted/30 rounded-lg">
                        <Mail className="h-5 w-5 text-muted-foreground" />
                        <div>
                          <p className="text-sm text-muted-foreground">{t("email")}</p>
                          <p className="font-medium">{client.email}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3 p-4 bg-muted/30 rounded-lg">
                        <Phone className="h-5 w-5 text-muted-foreground" />
                        <div>
                          <p className="text-sm text-muted-foreground">{t("phone")}</p>
                          <p className="font-medium">{client.phone || t("profileNoPhone")}</p>
                        </div>
                      </div>
                    </div>
                    <div className="space-y-6">
                      <div className="flex items-center gap-3 p-4 bg-muted/30 rounded-lg">
                        <Building2 className="h-5 w-5 text-muted-foreground" />
                        <div>
                          <p className="text-sm text-muted-foreground">{t("company")}</p>
                          <p className="font-medium">{client.company || t("profileNoCompany")}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3 p-4 bg-muted/30 rounded-lg">
                        <Target className="h-5 w-5 text-muted-foreground" />
                        <div>
                          <p className="text-sm text-muted-foreground">{t("status")}</p>
                          <div className="font-medium">
                            {getStatusBadge(client.status)}
                          </div>
                        </div>
                      </div>
                      {/* Nombre de projets */}
                      <div className="flex items-center gap-3 p-4 bg-muted/30 rounded-lg">
                        <Briefcase className="h-5 w-5 text-muted-foreground" />
                        <div>
                          <p className="text-sm text-muted-foreground">{t("projects")}</p>
                          <p className="font-medium">
                            {t("project", { count: client.projects })}
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Notes améliorées */}
          <TabsContent value="notes">
            <Card>
              <CardHeader className="bg-muted/50 border-b">
                <CardTitle className="flex items-center gap-2 text-lg">
                  <MessageSquare className="h-5 w-5 text-primary" />
                  {t("profileNotes")}
                </CardTitle>
                <CardDescription>
                  {t("profileNotesDesc")}
                </CardDescription>
              </CardHeader>
              <CardContent className="p-6">
                {isEditing ? (
                  <div className="space-y-4">
                    <Label htmlFor="note" className="text-sm font-medium">{t("notes")}</Label>
                    <Textarea
                      id="note"
                      value={formData.note || ""}
                      onChange={(e) => setFormData(prev => ({ ...prev, note: e.target.value }))}
                      className="min-h-[200px]"
                      placeholder={t("profileNotesPlaceholder")}
                    />
                  </div>
                ) : (
                  <div className="space-y-4">
                    {client.note ? (
                      <div className="p-4 bg-muted/30 rounded-lg">
                        <p className="text-foreground whitespace-pre-wrap">{client.note}</p>
                      </div>
                    ) : (
                      <div className="text-center py-12 space-y-4">
                        <MessageSquare className="h-16 w-16 text-muted-foreground mx-auto" />
                        <div>
                          <h3 className="text-lg font-semibold text-foreground">{t("profileNoNotes")}</h3>
                          <p className="text-muted-foreground mt-1">
                            {t("profileNoNotesDesc")}
                          </p>
                        </div>
                        <Button 
                          onClick={() => setIsEditing(true)}
                          className="mt-4"
                        >
                          <Edit className="mr-2 h-4 w-4" />
                          {t("profileAddNote")}
                        </Button>
                      </div>
                    )}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </AgencyLayout>
  );
}