// src/types/offers.ts
import { Client } from "@/types/clients";

export interface Offer {
  id: number;
  title: string;
  description?: string;
  price?: number;
  currency?: string;
  category?: string;
  status: "active" | "inactive" | "pending";
  validUntil?: string;
  delivery_date?: string;
  avatar?: string;
  seller_profile?: string;
  condition_state?: "new" | "like_new" | "good" | "fair" | "poor" | "to_repair";
  tags?: string[];
  reviews?: Array<{
    rating: number;
    comment: string;
    user_name?: string;
  }>;
   images?: string[];      
  localisation?: string; 
  clientsCount?: number;
  clients?: Pick<Client, "id" | "name" | "company" | "email" | "avatar">[];
  created_at?: string;
  updated_at?: string;
}